import React, { useState, useEffect } from "react";
import "./App.css";
import "@aws-amplify/ui-react/styles.css";
import { API } from "aws-amplify";
import {
  Button,
  Flex,
  Heading,
  Text,
  TextField,
  View,
  withAuthenticator,
} from "@aws-amplify/ui-react";
import { listAttendees } from "./graphql/queries";
import {
  createAttendees as createAttendeesMutation
} from "./graphql/mutations";

const App = () => {
  const [attendees, setAttendees] = useState([]);

  useEffect(() => {
    fetchAttendees();
  }, []);

  async function fetchAttendees() {
  const apiData = await API.graphql({ query: listAttendees });
  const attendeesFromAPI = apiData.data.listAttendees.items;
  setAttendees(attendeesFromAPI);
  }

  async function createAttendees(event) {
    event.preventDefault();
    const form = new FormData(event.target);
    const data = {
      attendeeFirstName: form.get("fname"),
      attendeeLastName: form.get("lname"),
      attendeeContact: form.get("contact"),
      registrationTimestamp: Date.now()
    };
    await API.graphql({
      query: createAttendeesMutation,
      variables: { input: data },
    });
    fetchAttendees();
    event.target.reset();
  }

  return (
    <View className="App">
      <Heading level={1}>Soccer Registration</Heading>
      <br />
      <hr />
      <Heading level={2}>Register</Heading>
      <View as="form" margin="3rem 0" onSubmit={createAttendees}>
        <Flex direction="row" justifyContent="center">
          <TextField
            name="fname"
            placeholder="First Name"
            label="First Name"
            labelHidden
            variation="quiet"
            required
          />
          <TextField
            name="lname"
            placeholder="Last Name"
            label="Last Name"
            labelHidden
            variation="quiet"
            required
          />
          <TextField
            name="contact"
            placeholder="Contact"
            label="Contact"
            labelHidden
            variation="quiet"
            required
          />
          <Button type="submit" variation="primary">
            Shoot
          </Button>
        </Flex>
      </View>
      <Heading level={2}>Current Attendees</Heading>
      <View margin="3rem 0">
      {attendees.map((attendee) => (
        <Flex
          key={attendee.id || attendee.attendeeContact}
          direction="row"
          justifyContent="left"
          alignItems="centre"
        >
          <Text as="strong" fontWeight={700}>
            {attendee.attendeeNo}
          </Text>
          <Text as="span">
            {attendee.attendeeFirstName}
          </Text>
          <Text as="span">
            {attendee.attendeeLastName}
          </Text>
          <Text as="span">
            {attendee.attendeeContact}
          </Text>
          <Text as="span">
            {attendee.registrationTimestamp}
          </Text>
        </Flex>
      ))}
      </View>
    </View>
  );
}

export default App;
